mapper.insert:
insert into tb_board
			(	
				board_id

			  , board_type


			  , board_title


			  , board_contents


			  , url


			  , created_id

			)
			values
			(
				FNC_ID(#{boardId})

			  , #{boardType}


			  , #{boardTitle}


			  , #{boardContents}


			  , #{url}


			  , #{createdId}

			)

mapper.update:
update tb_board
   set updated_datetime = now()

     , updated_id = #{updatedId}


     , board_title = #{boardTitle}


     , board_contents = #{boardContents}


     , url = #{url}

 where board_id = #{boardId}
