mapper.select:
/* BoardMapper.selectBoard */
select count(1) over() as total_count , row_number() over(order by board_id desc ) as rnum , tb.* , tf.file_nm as thumb_nm , tf.file_origin_nm as thumb_origin_nm , tf.file_size as thumb_size from ( select board_id , board_type , board_title , board_contents , url , created_id , DATE_FORMAT(created_datetime, '%Y-%m-%d') as created_datetime , updated_id , DATE_FORMAT(updated_datetime, '%Y-%m-%d') as updated_datetime from tb_board where board_id like concat(#{id},'%')
and board_title like concat('%',#{searchKeyword},'%') and board_contents like concat('%',#{searchKeyword},'%')
and ( board_title like concat('%',#{searchKeyword},'%') or board_contents like concat('%',#{searchKeyword},'%') )
)tb left outer join tb_file tf on tb.board_id = tf.file_id and tf.file_type = '1' where 1 = 1

/* BoardMapper.selectBoardInfo */	
select *
  from tb_board
 where board_id = #{id}

mapper.insert:
	/* BoardMapper.insertBoard */

        SELECT FNC_ID(#{boardId})       

	insert into tb_board
				(	
					board_id

				  , board_type


				  , board_title


				  , board_contents


				  , url


				  , created_id

				)
				values
				(
					FNC_ID(#{boardId})

				  , #{boardType}


				  , #{boardTitle}


				  , #{boardContents}


				  , #{url}


				  , #{createdId}

				)

mapper.update:
/* BoardMapper.updateBoard */	
update tb_board
   set updated_datetime = now()

     , updated_id = #{updatedId}


     , board_title = #{boardTitle}


     , board_contents = #{boardContents}


     , url = #{url}


     , board_type = #{boardType}

 where board_id = #{boardId}

mapper.delete:
	/* BoardMapper.deleteBoard */	
	delete 
 	  from tb_board
 	 where board_id = #{id}

/* BoardMapper.deleteBoardIds */		
delete from tb_board where board_id in 

	#{id}
