mapper.select:
/* BoardMapper.selectBoard */
select count(1) over() as total_count , row_number() over(order by board_id desc ) as rnum , tb.* from ( select board_id , board_type , board_title , board_contents , url , created_id , DATE_FORMAT(created_datetime, '%Y-%m-%d') as created_datetime , updated_id , DATE_FORMAT(updated_datetime, '%Y-%m-%d') as updated_datetime from tb_board where board_id like concat(#{id},'%')
and board_title like concat('%',#{searchKeyword},'%')
)tb where 1 = 1

/* BoardMapper.selectBoardInfo */	
select *
  from tb_board
 where board_id = #{id}

mapper.insert:
/* BoardMapper.insertBoard */	
insert into tb_board
			(	
				board_id

			  , board_type


			  , board_title


			  , board_contents


			  , url


			  , created_id

			)
			values
			(
				FNC_ID(#{boardId})

			  , #{boardType}


			  , #{boardTitle}


			  , #{boardContents}


			  , #{url}


			  , #{createdId}

			)

mapper.update:
/* BoardMapper.updateBoard */	
update tb_board
   set updated_datetime = now()

     , updated_id = #{updatedId}


     , board_title = #{boardTitle}


     , board_contents = #{boardContents}


     , url = #{url}

 where board_id = #{boardId}

mapper.delete:
	/* BoardMapper.deleteBoard */	
	delete 
 	  from tb_board
 	 where board_id = #{id}

/* BoardMapper.deleteBoardIds */		
delete from tb_board where board_id in 

	#{id}
