mapper.select:
/* ConactUsMapper.selecConactUsInfo */	
select *
  from tb_conact_us
 where conact_id = #{id}

mapper.insert:
/* ConactUsMapper.insertConactUs */	
insert into tb_conact_us
			(	
				conact_id

			  , conact_nm


			  , conact_phone


			  , conact_mail


			  , conact_contents


			  , conact_agree

			)
			values
			(
				FNC_ID(#{conactId})

			  , #{conactNm}


			  , #{conactPhone}


			  , #{conactMail}


			  , #{conactContents}


			  , #{conactAgree}

			)

mapper.update:
/* ConactUsMapper.updateConactUs */	
update tb_conact_us
   set conact_agree = 'Y'

     , conact_nm = #{conactNm}


     , conact_phone = #{conactPhone}


     , conact_mail = #{conactMail}


     , conact_contents = #{conactContents}

 where conact_id = #{conactId}

mapper.delete:
	/* ConactUsMapper.deleteConactUs */	
	delete 
 	  from tb_conact_us
 	 where conact_id = #{id}

/* PopupMapper.deleteConactUsIds */		
delete from tb_conact_us where conact_id in 

	#{id}
