mapper.select:
/* ContactUsMapper.selecContactUsInfo */	
select *
  from tb_contact_us
 where contact_id = #{id}

mapper.insert:
	/* ContactUsMapper.insertContactUs */

        SELECT FNC_ID(#{contactId})       

	insert into tb_contact_us
				(	
					contact_id

				  , contact_nm


				  , contact_title


				  , contact_phone


				  , contact_mail


				  , contact_contents


				  , contact_agree

				)
				values
				(
					FNC_ID(#{contactId})

				  , #{contactNm}


				  , #{contactTitle}


				  , #{contactPhone}


				  , #{contactMail}


				  , #{contactContents}


				  , #{contactAgree}

				)

mapper.delete:
	/* ContactUsMapper.deleteContactUs */	
	delete 
 	  from tb_contact_us
 	 where contact_id = #{id}

/* PopupMapper.deleteContactUsIds */		
delete from tb_contact_us where contact_id in 

	#{id}
