mapper.select:
/* ContactUsMapper.selectContactUs */
select count(1) over() as total_count , row_number() over(order by contact_id desc ) as rnum , tb.* from ( select tb.contact_id , tb.contact_type , tb.contact_nm , replace(tb.contact_nm2, substr(tb.contact_nm2, 2, 1), '*') contact_nm2 , tb.contact_title , tb.contact_phone , tb.contact_mail , tb.contact_contents , tb.contact_recontents , tb.contact_agree , tb.created_datetime , tb.contact_process from ( select contact_id , contact_type , CAST(AES_DECRYPT(UNHEX(contact_nm), SHA2(#{encryptKey}, 256)) AS CHAR) as contact_nm , CAST(AES_DECRYPT(UNHEX(contact_nm), SHA2(#{encryptKey}, 256)) AS CHAR) as contact_nm2 , contact_title , CAST(AES_DECRYPT(UNHEX(contact_phone), SHA2(#{encryptKey}, 256)) AS CHAR) as contact_phone , CAST(AES_DECRYPT(UNHEX(contact_mail), SHA2(#{encryptKey}, 256)) AS CHAR) as contact_mail , contact_contents , contact_recontents , contact_pw , contact_agree , DATE_FORMAT(created_datetime, '%Y-%m-%d') as created_datetime , contact_process from tb_contact_us where 1 = 1
and contact_id like concat(#{id},'%')
and contact_type = #{contactType}
and CAST(AES_DECRYPT(UNHEX(contact_mail), SHA2(#{encryptKey}, 256)) AS CHAR) = #{contactMail}
and CAST(AES_DECRYPT(UNHEX(contact_nm), SHA2(#{encryptKey}, 256)) AS CHAR) like concat('%',#{searchKeyword},'%') and contact_title like concat('%',#{searchKeyword},'%') and contact_contents like concat('%',#{searchKeyword},'%')
and ( CAST(AES_DECRYPT(UNHEX(contact_nm), SHA2(#{encryptKey}, 256)) AS CHAR) like concat('%',#{searchKeyword},'%') or contact_title like concat('%',#{searchKeyword},'%') or contact_contents like concat('%',#{searchKeyword},'%') )
) tb )tb where 1 = 1

/* ContactUsMapper.selectContactUsInfoCnt */	
select count(1) cnt
  from tb_contact_us
 where 1 = 1

   and contact_id = #{id}


   and contact_type = #{contactType}


   and CAST(AES_DECRYPT(UNHEX(contact_mail), SHA2(#{encryptKey}, 256)) AS CHAR) = #{contactMail}


   and CAST(AES_DECRYPT(UNHEX(contact_pw), SHA2(#{encryptKey}, 256)) AS CHAR) = #{contactPw}

	/* ContactUsMapper.selectPrevNextContactUs */	

  select (select contact_id from tb_contact_us where contact_id < #{contactId} and contact_id and CAST(AES_DECRYPT(UNHEX(contact_mail), SHA2(#{encryptKey}, 256)) AS CHAR) = #{contactMail} like concat(left(#{contactId},3),'%') order by contact_id desc limit 1) as prev_board_id
	   , (select contact_id from tb_contact_us where contact_id > #{contactId} and contact_id and CAST(AES_DECRYPT(UNHEX(contact_mail), SHA2(#{encryptKey}, 256)) AS CHAR) = #{contactMail} like concat(left(#{contactId},3),'%') order by contact_id limit 1) as next_board_id
	from tb_contact_us
   where 1=1
	 and contact_id = #{contactId}

mapper.insert:
/* ContactUsMapper.insertContactUs */
SELECT FNC_ID(#{contactId})
insert into tb_contact_us ( contact_id
, contact_type
, contact_nm
, contact_title
, contact_phone
, contact_mail
, contact_contents
, contact_recontents
, contact_pw
, contact_agree
) values ( FNC_ID(#{contactId})
, #{contactType}
,HEX(AES_ENCRYPT(#{contactNm}, SHA2(#{encryptKey}, 256)))
, #{contactTitle}
,HEX(AES_ENCRYPT(#{contactPhone}, SHA2(#{encryptKey}, 256)))
,HEX(AES_ENCRYPT(#{contactMail}, SHA2(#{encryptKey}, 256)))
, #{contactContents}
, #{contactRecontents}
,HEX(AES_ENCRYPT(#{contactPw}, SHA2(#{encryptKey}, 256)))
, #{contactAgree}
)

mapper.delete:
	/* ContactUsMapper.deleteContactUs */	
	delete 
 	  from tb_contact_us
 	 where contact_id = #{id}

mapper.update:
/* ContactUsMapper.updateContactUs */		
update tb_contact_us
   set contact_process = 'Y' 
     , contact_recontents = #{contactRecontents}
 where contact_id = #{contactId}

/* ContactUsMapper.updateContactUsIds */		
update tb_contact_us
   set contact_process = 'Y' 
 where contact_id in 

	#{id}

mapper.delete:
/* ContactUsMapper.deleteContactUsIds */		
delete from tb_contact_us where contact_id in 

	#{id}
