mapper.select:
/* ContactUsEngMapper.selectEngContactUs */
select count(1) over() as total_count , row_number() over(order by contact_id desc ) as rnum , tb.* from ( select contact_id , CAST(AES_DECRYPT(UNHEX(contact_nm), SHA2(#{encryptKey}, 256)) AS CHAR) as contact_nm , contact_title , CAST(AES_DECRYPT(UNHEX(contact_phone), SHA2(#{encryptKey}, 256)) AS CHAR) as contact_phone , CAST(AES_DECRYPT(UNHEX(contact_mail), SHA2(#{encryptKey}, 256)) AS CHAR) as contact_mail , contact_contents , contact_agree , DATE_FORMAT(created_datetime, '%Y-%m-%d') as created_datetime from tb_contact_us_en where contact_id like concat(#{id},'%') and contact_type = #{contactType}
and CAST(AES_DECRYPT(UNHEX(contact_nm), SHA2(#{encryptKey}, 256)) AS CHAR) like concat('%',#{searchKeyword},'%') and contact_title like concat('%',#{searchKeyword},'%') and contact_contents like concat('%',#{searchKeyword},'%')
and ( CAST(AES_DECRYPT(UNHEX(contact_nm), SHA2(#{encryptKey}, 256)) AS CHAR) like concat('%',#{searchKeyword},'%') or contact_title like concat('%',#{searchKeyword},'%') or contact_contents like concat('%',#{searchKeyword},'%') )
)tb where 1 = 1

mapper.insert:
/* ContactUsEngMapper.insertEngContactUs */
SELECT FNC_ID_EN(#{contactId})
insert into tb_contact_us_en ( contact_id
, contact_type
, contact_nm
, contact_title
, contact_phone
, contact_mail
, contact_contents
, contact_agree
) values ( FNC_ID_EN(#{contactId})
, #{contactType}
,HEX(AES_ENCRYPT(#{contactNm}, SHA2(#{encryptKey}, 256)))
, #{contactTitle}
,HEX(AES_ENCRYPT(#{contactPhone}, SHA2(#{encryptKey}, 256)))
,HEX(AES_ENCRYPT(#{contactMail}, SHA2(#{encryptKey}, 256)))
, #{contactContents}
, #{contactAgree}
)

mapper.delete:
	/* ContactUsEngMapper.deleteEngContactUs */	
	delete 
 	  from tb_contact_us_en
 	 where contact_id = #{id}

/* ContactUsEngMapper.deleteEngContactUsIds */		
delete from tb_contact_us_en where contact_id in 

	#{id}
