mapper.select:
/* FileMapper.selectFileInfo */
select *
  from tb_file
 where file_nm = #{fileNm}

/* FileMapper.selectFileList */
select *
  from tb_file
 where file_id = #{fileId}

mapper.insert:
/* FileMapper.insertFile */
insert into tb_file
			(	
				file_id

			  , file_type


			  , file_path


			  , file_nm


			  , file_origin_nm


			  , file_size

			)
			values
			(
				#{fileId}

			  , #{fileType}


			  , #{filePath}


			  , #{fileNm}


			  , #{fileOriginNm}


			  , #{fileSize}

			)

mapper.delete:
	/* FileMapper.deleteFile */
	delete 
 	  from tb_file
 	 where file_id = #{id}

/* FileMapper.deleteFileForName */
delete from tb_file where file_nm in 

	#{name}

/* FileMapper.deleteFileIds */		
delete from tb_file where file_id in 

	#{id}

	/* FileMapper.deleteThumbnail */
	delete 
 	  from tb_file
 	 where file_id = #{id}
 	 and file_type = '1'

	/* FileMapper.deleteOtherFile */
	delete 
 	  from tb_file
 	 where file_id = #{id}
 	 and file_type != '1'
