mapper.insert:
/* OutlineEngMapper.insertEngOutline */
insert into tb_company_outline
(	
	company_year
	,company_month
	,company_day
	,company_contents
	,created_id
)
values
(
	#{companyYear}
	, #{companyMonth}
	, #{companyDay}
	, #{companyContents}
	, #{createdId}
)

mapper.update:
/* OutlineEngMapper.updateEngOutline */	
update tb_company_outline
   set updated_datetime = now()

     , updated_id = #{updatedId}


     , company_year = #{companyYear}


     , company_month = #{companyMonth}


     , company_day = #{companyDay}


     , company_contents = #{companyContents}

 where company_id = #{companyId}

mapper.delete:
/* OutlineEngMapper.deleteEngOutline */		
delete from tb_company_outline where company_id in 

	#{id}
