mapper.select:
/* PopupMapper.selectPopupInfo */	
select *
  from tb_popup
 where popup_id = #{id}

mapper.insert:
/* PopupMapper.insertPopup */	
insert into tb_popup
			(	
				popup_id

			  , popup_title


			  , popup_link


			  , popup_close1


			  , popup_close2


			  , popup_height


			  , popup_width


			  , popup_startdate


			  , popup_enddate


			  , created_id

			)
			values
			(
				FNC_ID(#{popupId})

			  , #{popupTitle}


			  , #{popupLink}


			  , #{popupClose1}


			  , #{popupClose2}


			  , #{popupHeight}


			  , #{popupWidth}


			  , #{popupStartdate}


			  , #{popupEnddate}


			  , #{createdId}

			)

mapper.update:
/* PopupMapper.updatePopup */	
update tb_popup
   set updated_datetime = now()

     , updated_id = #{updatedId}


     , popup_title = #{popupTitle}


     , popup_link = #{popupLink}


     , popup_close1 = #{popupClose1}


     , popup_close2 = #{popupClose2}


     , popup_height = #{popupHeight}


     , popup_width = #{popupWidth}


     , popup_startdate = #{popupStartdate}


     , popup_enddate = #{popupEnddate}

 where popup_id = #{popupId}

mapper.delete:
	/* PopupMapper.deletePopup */	
	delete 
 	  from tb_popup
 	 where popup_id = #{id}

/* PopupMapper.deletePopupIds */		
delete from tb_popup where popup_id in 

	#{id}
