mapper.select:
/* PopupEngMapper.selectEngPopup */
select count(1) over() as total_count , row_number() over(order by popup_id desc ) as rnum , tb.* from ( select pop.popup_id , pop.popup_title , pop.popup_link , pop.popup_close1 , pop.popup_close2 , pop.popup_height , pop.popup_width , pop.popup_startdate , pop.popup_enddate , pop.created_id , DATE_FORMAT(pop.created_datetime, '%Y-%m-%d') as created_datetime , pop.updated_id , DATE_FORMAT(pop.updated_datetime, '%Y-%m-%d') as updated_datetime , fil.thumb_nm , fil.thumb_origin_nm , fil.thumb_size from tb_popup pop left join ( select file_id , file_nm as thumb_nm , file_origin_nm as thumb_origin_nm , file_size as thumb_size from tb_file ) fil on pop.popup_id = fil.file_id where pop.popup_id like concat(#{id},'%') )tb where 1 = 1
and tb.popup_startdate <= DATE_FORMAT(now(), '%Y-%m-%d') and tb.popup_enddate >= DATE_FORMAT(now(), '%Y-%m-%d')

/* PopupEngMapper.selectEngPopupInfo */	
select *
  from tb_popup
 where popup_id = #{id}

mapper.insert:
	/* PopupEngMapper.insertEngPopup */	

        SELECT FNC_ID(#{popupId})       

	insert into tb_popup
				(	
					popup_id

				  , popup_title


				  , popup_link


				  , popup_close1


				  , popup_close2


				  , popup_height


				  , popup_width


				  , popup_startdate


				  , popup_enddate


				  , created_id

				)
				values
				(
					FNC_ID(#{popupId})

				  , #{popupTitle}


				  , #{popupLink}


				  , #{popupClose1}


				  , #{popupClose2}


				  , #{popupHeight}


				  , #{popupWidth}


				  , #{popupStartdate}


				  , #{popupEnddate}


				  , #{createdId}

				)

mapper.update:
/* PopupEngMapper.updateEngPopup */	
update tb_popup
   set updated_datetime = now()

     , updated_id = #{updatedId}


     , popup_title = #{popupTitle}


     , popup_link = #{popupLink}


     , popup_close1 = #{popupClose1}


     , popup_close2 = #{popupClose2}


     , popup_height = #{popupHeight}


     , popup_width = #{popupWidth}


     , popup_startdate = #{popupStartdate}


     , popup_enddate = #{popupEnddate}

 where popup_id = #{popupId}

mapper.delete:
	/* PopupEngMapper.deleteEngPopup */	
	delete 
 	  from tb_popup
 	 where popup_id = #{id}

/* PopupEngMapper.deleteEngPopupIds */		
delete from tb_popup where popup_id in 

	#{id}
