mapper.insert:
/* UserMapper.insertUser */
insert into tb_user
			(	
				telno

			  , telcom


			  , gender


			  , brth


			  , ci

			)
			values
			(
				HEX(AES_ENCRYPT(#{telno},  SHA2(#{encryptKey}, 256)))

			  , #{telcom}


			  , #{gender}


			  , HEX(AES_ENCRYPT(#{brth},  SHA2(#{encryptKey}, 256)))


			  , #{ci}

			)
